// src/components/MissionDetail.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MissionDetail = ({ missionId }) => {
    const [mission, setMission] = useState(null);
    const baseUrl = process.env.REACT_APP_API_BASE_URL;

    useEffect(() => {
        axios.get(`${baseUrl}/mission/${missionId}`)
            .then(response => {
                setMission(response.data);
            })
            .catch(error => {
                console.error('There was an error fetching the mission!', error);
            });
    }, [missionId, baseUrl]);

    if (!mission) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2>{mission.name}</h2>
            <p>{mission.description}</p>
            <h3>Robot</h3>
            <p>Name: {mission.robot.name}</p>
            <p>Model: {mission.robot.model_name}</p>
        </div>
    );
};

export default MissionDetail;
