// src/components/MissionForm.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MissionForm = () => {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [robotId, setRobotId] = useState('');
    const [robots, setRobots] = useState([]);
    const baseUrl = process.env.REACT_APP_API_BASE_URL;

    useEffect(() => {
        axios.get(`${baseUrl}/robots/`)
            .then(response => {
                setRobots(response.data);
            })
            .catch(error => {
                console.error('There was an error fetching the robots!', error);
            });
    }, [baseUrl]);

    const handleSubmit = (event) => {
        event.preventDefault();

        axios.post(`${baseUrl}/missions/`, { name, description, robot_id: robotId })
            .then(response => {
                console.log('Mission created:', response.data);
            })
            .catch(error => {
                console.error('There was an error creating the mission!', error);
            });
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Create Mission</h2>
            <label>
                Name:
                <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
            </label>
            <br />
            <label>
                Description:
                <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
            </label>
            <br />
            <label>
                Robot:
                <select value={robotId} onChange={(e) => setRobotId(e.target.value)}>
                    <option value="">Select a robot</option>
                    {robots.map(robot => (
                        <option key={robot.id} value={robot.id}>
                            {robot.name}
                        </option>
                    ))}
                </select>
            </label>
            <br />
            <button type="submit">Create</button>
        </form>
    );
};

export default MissionForm;
